import React from 'react';
import './App.css';
import {BrowserRouter as Router,Route,Link} from 'react-router-dom'
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar'; 
import Nav from 'react-bootstrap/Nav'; 

import HomePage from './pages/HomePage.js';
import AboutPage from './pages/AboutPage.js';
import ContactPage from './pages/ContactPage.js';
import Footer from './components/Footer.js';
class App extends React.Component
{  
  constructor(props)
  {
    super(props); 
    this.state = 
    {
      title: 'Shaan Hossain',
      headerLinks: [
       {title: 'Home', path: '/'},
       {title: 'About', path: '/about'},
       {title: 'Contact', path: '/contact'},
      ], 
        home : {
        title: "Be forward-thinking",
        subTitle: "Creating Projects that are ",
        text: "Checkout my projects below",
        } ,
        about : {
          title: "About me",
          } ,
         contact : {
            title: "Want to Collaborate?", 
            subTitle: "Let's talk", 
            } 
      }
    }
    render()
 {
   return (
     <Router>
     <Container className = "p-0" fluid = {true}>
     <Navbar className = "border-bottom" bg= "dark" variant = "dark" expand = "lg">
      <Navbar.Brand> Shaan Hossain </Navbar.Brand>
      <Navbar.Toggle classname= "border-1" aria-controls ="navbar-toggle"/>
      <Navbar.Collapse id = "navbar-toggle">
       <Nav className = "ml-auto">
       <Link className = "nav-link" bg="light" to="/">Home</Link>
       <Link className = "nav-link" to="/about">About</Link>
       <Link className = "nav-link" to="/contact">Contact</Link>

       </Nav>
      </Navbar.Collapse> 
     </Navbar>

          <Route path="/" exact render={() => <HomePage title={this.state.home.title} subTitle={this.state.home.subTitle} text={this.state.home.text} />} />
          <Route path="/about" render={() => <AboutPage title={this.state.about.title} />} />
          <Route path="/contact" render={() => <ContactPage title={this.state.contact.title} subTitle={this.state.contact.subTitle}  />} />
      <Footer />

     </Container>
     </Router>
   ); 
 }
  }




export default App;
